const handleSubmit = async () => {
  setLoading(true);
  setError('');
  setResult(null);

  try {
    const response = await axios.post('http://localhost:5000/analyze', { text });
    setResult(response.data as { emotion: string; confidence: number });
  } catch {
    setError('Error reaching the server');
  } finally {
    setLoading(false);
  }
};
